def getAsignatures():
    file = open('output.txt','r')
    mensaje = file.read()
    file.close()
    #print(mensaje[6:-3])
    strMatrix = mensaje[6:-2].split('\n|')
    for i in range(len(strMatrix)):
        strMatrix[i] = strMatrix[i].split(', ')
        for j in range(len(strMatrix[i])):
            strMatrix[i][j] = int(strMatrix[i][j])
    #print(strMatrix)
    #for i in strMatrix:
    #   print(i)
            

    materiasHoras = []
    for i in range(len(strMatrix)):
        for j in range(len(strMatrix[i])):
            if(strMatrix[i][j]!=0):
                if(not inTupleArray(materiasHoras,strMatrix[i][j])):
                    materiasHoras.append(obtenerHoras(i,j,strMatrix))
    print(materiasHoras)
    return materiasHoras


getAsignatures()
